import { rxFromStream, txToStream } from "./rxtx-stream_browser.js";
import { Transport } from "./transport_browser.js";
/** Stream-oriented transport. */
export class StreamTransport extends Transport {
    rx;
    tx;
    constructor(conn, attrs = {}) {
        super(attrs);
        this.rx = rxFromStream(conn);
        this.tx = txToStream(conn);
    }
    get mtu() { return Infinity; }
}
